con:config.script:
import groovy.json.JsonSlurper

def filepath = "C:\\Data\\API\\data_different_options.json"

File dataFile = new File(filepath)

def filecontent = dataFile.getText()

def jsonData = new JsonSlurper().parseText(filecontent)

def clog(prop, value) {
	log.info("Property '" + prop + "' changed to '" + value + "'")
}

def jsonDataLength = jsonData.size()
for (int i = 0; i < jsonDataLength; i++){
	testRunner.testCase.getTestStepByName("Properties").setPropertyValue("inputText", jsonData[i].inputText)
	clog("inputText", jsonData[i].inputText)
	testRunner.testCase.getTestStepByName("Properties").setPropertyValue("expected", jsonData[i].expected)
	clog("expected", jsonData[i].expected)

	def runFromTestRunner = testRunner.testCase.getTestStepByName("Properties").getPropertyValue("testRunner")
	if (runFromTestRunner.equals("true") && i == jsonDataLength - 1) {
		break;
	}

	log.info("    >>>>    Start Test Run #" + (i+1) + "    <<<<    ")

	testRunner.getTestCase().getTestStepList().each{
		if (!it.name.equals("loadData")){
			log.info("Run step - " + it.name)
			testRunner.runTestStepByName(it.name)
		}
	}
	log.info("    >>>>    Finish Test Run #" + (i+1) + "    <<<<    ")
}

